                            dp[i] = (dp[i] + 1) % MOD
                else:
                    if len(one_char) == 1:
                        if one_char != "0011" and one_char != "0101" and one_char != "1110" and one_char != "1111":
                            dp[i] = (dp[i] + 1) % MOD

        if i >= 2:
            two_char = s_list[i - 1] + s_list[i - 2]
            if len(two_char) == 2:
                if two_char != "0011" and two_char != "0101" and two_char != "1110" and two_char != "1111":
                    dp[i] = (dp[i] + 1) % MOD

        if i >= 3:
            three_char = s_list[i - 1] + s_list[i - 2] + s_list[i - 3]
            if len(three_char) == 3:
                if three_char != "0011" and three_char != "0101" and three_char != "1110" and three_char != "1111":
                    dp[i] = (dp[i] + 1) % MOD

        if i >= 4:
            four_char = s_list[i - 1] + s_list[i - 2] + s_list[i - 3] + s_list[i - 4]
            if len(four_char) == 4:
                if four_char != "0011" and four_char != "0101" and four_char != "1110" and four_char != "1111":
                    dp[i] = (dp[i] + 1) % MOD

        total_sequences = (total_sequences + dp[i]) % MOD
        ans.append(total_sequences)

    for a in ans:
        print(a)

