import sys

def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    odd_a = sorted([x for x in a if x % 2 == 1])
    even_a = sorted([x for x in a if x % 2 == 0])

    odd_b = sorted([x for x in b if x % 2 == 1])
    even_b = sorted([x for x in b if x % 2 == 0])

    i, j = 0, 0
    count = 0
    while i < len(odd_a) and j < len(odd_b):
        if odd_a[i] + odd_b[j] == 1:
            count += 1
            i += 1
            j += 1
        elif odd_a[i] + odd_b[j] > 1:
            j += 1
        else:
            i += 1

    i, j = 0, 0
    while i < len(even_a) and j < len(even_b):
        if even_a[i] + even_b[j] == 1:
            count += 1
            i += 1
            j += 1
        elif even_a[i] + even_b[j] > 1:
            j += 1
        else:
            i += 1

    print(count)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
