from sys import stdin, stdout

def has_koutsu(hand):
    """Check if the hand contains a koutsu."""
    for suit in ['m', 'p', 's']:
        count = sum(1 for tile in hand if tile[1] == suit)
        if count >= 3:
            return True
    return False

def has_shuntsu(hand):
    """Check if the hand contains a shuntsu."""
    hand.sort()
    for i in range(len(hand) - 2):
        if hand[i][0] == hand[i+1][0] == hand[i+2][0] and int(hand[i][0]) + 1 == int(hand[i+1][0]) and int(hand[i+1][0]) + 1 == int(hand[i+2][0]):
            return True
    return False

def solve(hand):
    """Solve the problem."""
    if has_koutsu(hand) or has_shuntsu(hand):
        return 0
    else:
        return 1

# Read input from standard input.
hand1, hand2, hand3 = stdin.readline().split()

# Solve the problem and print the result to standard output.
stdout.write(str(solve(hand1.split()) + '\n'))
stdout.write(str(solve(hand2.split()) + '\n'))
stdout.write(str(solve(hand3.split()) + '\n'))
