def count_beautiful_strings(n, m, g):
    MOD = 10**9 + 7
    if n < m:
        n, m = m, n
        g ^= 1

    dp = [[0] * (m + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        for j in range(min(i, m) + 1):
            if j == 0:
                dp[i][j] = dp[i - 1][j]
            else:
                dp[i][j] = (dp[i - 1][j] + dp[i - 2][j - 1]) % MOD

    return dp[n][m]

n, m, g = map(int, input().split())
print(count_beautiful_strings(n, m, g))
