MOD = 10**9 + 7

def solve():
    n, m, g = map(int, input().split())

    if g == 0:
        if n > m:
            return (pow(2, m, MOD) * (n - m + 1)) % MOD
        else:
            return pow(2, n, MOD)
    else:
        return 0

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        print(solve())
