def power_mod(a, n, mod):
    """Compute a^n mod mod."""
    result = 1
    while n > 0:
        if n % 2 == 1:
            result = (result * a) % mod
        a = (a * a) % mod
        n //= 2
    return result

def main():
    import sys
    input = sys.stdin.readline

    MOD = 1000000007

    for _ in range(int(input())):
        n, m, g = map(int, input().split())

        if n + m < 2:
            print(0)
            continue

        if g == 0:
            if n == m:
                print(power_mod(n + m - 1, n + m - 1, MOD))
            else:
                print((power_mod(m, n + m, MOD) - 1) * power_mod(n + m - 1, n + m - 2, MOD) % MOD)
        elif g == 1:
            print(0)

if __name__ == "__main__":
    main()
