MOD = 10**9 + 7

def process_string(s, queries):
    """Process a string by applying a list of queries."""
    # Convert the string to a list of integers for easier manipulation
    s = [int(c) for c in s]

    # Create a dictionary to store the replacements
    replacements = {}
    for query in queries:
        di, ti = query.split('->')
        di = int(di)
        if ti == '':
            ti = '0'
        replacements[di] = [int(c) for c in ti]

    # Apply the replacements
    for di, ti in sorted(replacements.items(), reverse=True):
        for i in range(len(s)):
            if s[i] == di:
                s[i:i+1] = ti

    # Convert the list back to a string and remove leading zeros
    s = ''.join(map(str, s))
    s = s.lstrip('0')

    # If the string is empty, return 0
    if not s:
        return 0

    # Calculate the remainder modulo MOD
    result = 0
    for c in s:
        result = (result * 10 + int(c)) % MOD
    return result

def main():
    s = input()
    n = int(input())
    queries = []
    for _ in range(n):
        queries.append(input())

    print(process_string(s, queries))

if __name__ == "__main__":
    main()
