MOD = 10**9 + 7

def solve():
    s = input()
    n = int(input())
    mapping = {}

    for _ in range(n):
        query = input()
        di, ti = query.split('->')
        di = int(di)
        ti = ti or '0'

        if di not in mapping:
            mapping[di] = ti

        for i, c in enumerate(ti):
            s = s.replace(str(di), str(c) + s[i:])

    value = 0
    for digit in s:
        value = (value * 10 + int(digit)) % MOD

    print(value)

if __name__ == "__main__":
    solve()
