def check_x(n, grid):
    # Check if the first and last row are the same
    if not all(grid[i][0] == grid[i][-1] for i in range(n)):
        return "NO"

    # Check if the first and last column are the same
    if not all(grid[0][i] == grid[-1][i] for i in range(n)):
        return "NO"

    # Check if the first and last diagonal are the same
    if not all(grid[i][i] == grid[i][n - i - 1] for i in range(n)):
        return "NO"

    # Check if the middle row and column are the same
    middle_row = [grid[n // 2][i] for i in range(1, n - 1)]
    middle_col = [grid[i][n // 2] for i in range(1, n - 1)]

    if not all(middle_row) or not all(middle_col):
        return "NO"

    # Check if the middle row and column are different from the first/last row and column
    if grid[n // 2][0] == grid[n // 2][n - 1] or grid[0][n // 2] == grid[n - 1][n // 2]:
        return "NO"

    return "YES"


def main():
    n = int(input())
    grid = []

    for _ in range(n):
        row = input()
        grid.append(list(row))

    result = check_x(n, grid)
    print(result)


if __name__ == "__main__":
    main()
