def check_x(n, grid):
    # Check if the length of the grid is correct
    if len(grid) != n or any(len(row) != n for row in grid):
        raise ValueError("Invalid grid size")

    # Get the diagonal letters
    diag1 = [grid[i][i] for i in range(n)]
    diag2 = [grid[i][n-i-1] for i in range(n)]

    # Check if both diagonals have the same letter
    if set(diag1) != set(diag2) or len(set(diag1)) != 1:
        return False

    # Get the letter of the non-diagonal squares
    other = [grid[i][j] for i in range(n) for j in range(n) if i != j and i != n-j-1]

    # Check if all non-diagonal squares have the same letter and it's different from the diagonal letter
    return len(set(other)) == 1 and set(other) != set(diag1)


def main():
    n = int(input())
    grid = []
    for _ in range(n):
        grid.append(input())

    result = check_x(n, grid)
    print("YES" if result else "NO")


if __name__ == "__main__":
    main()
