import sys

def solve():
    n, l = map(int, input().split())
    a = sorted(map(int, input().split()))

    if len(set(a)) == 1:
        print(0.0)
        return

    d = (l - a[-1]) / 2
    for i in range(n - 1):
        d = min(d, (a[i + 1] - a[i]) / 2)

    left = 0
    right = l
    while right - left > 1e-9:
        mid = (left + right) / 2
        count = 0
        last = 0
        for i in range(n):
            if a[i] + mid >= last:
                count += 1
                last = a[i] + mid
        if count >= 2:
            left = mid
        else:
            right = mid

    print(left)


if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
