import sys

def read_input():
    n, l = map(int, input().split())
    a = list(map(int, input().split()))
    return n, l, a

def solve(n, l, a):
    a.sort()
    max_d = 0
    for i in range(1, n):
        max_d = max(max_d, (a[i] - a[i-1]) // 2)
    if a[0] != 0:
        max_d = max(max_d, a[0])
    if a[-1] != l:
        max_d = max(max_d, l - a[-1])
    return max_d

def main():
    t = 1  # int(input())
    for _ in range(t):
        n, l, a = read_input()
        print(f"{solve(n, l, a):.10f}")

if __name__ == "__main__":
    main()
```

