import sys
import math

def read_input():
    return sys.stdin.readline().strip()

def read_ints():
    return list(map(int, read_input().split()))

def read_floats():
    return list(map(float, read_input().split()))

def main():
    n, l = read_ints()
    a = sorted(read_ints())

    def check(d):
        i = 0
        while i < n and a[i] - (i > 0) <= d:
            i += 1
        j = n - 1
        while j >= 0 and a[j] + (j < n - 1) >= l - d:
            j -= 1
        return i > j

    left, right = 0, l
    while right - left > 1e-9:
        mid = (left + right) / 2
        if check(mid):
            right = mid
        else:
            left = mid
    print(right)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        main()
