from functools import lru_cache

def count_inversions(n, mod):
    @lru_cache(maxsize=None)
    def dp(i, j, k):
        if i == j:
            return 0
        ans = 0
        for x in range(i, j):
            ans += (k + 1) * dp(i, x, 0) * dp(x + 1, j, 1) % mod
            ans %= mod
        return ans

    ans = 0
    for i in range(1, n + 1):
        ans += (n - i) * dp(0, i - 1, 0) % mod * dp(i, n, 1) % mod
        ans %= mod
    return ans

n, mod = map(int, input().split())
print(count_inversions(n, mod))
