    return list(map(int, stdin.readline().split()))

def solve(n, h, m, restrictions):
    # Create a set of all restrictions for fast lookup
    restriction_set = {(l, r, x) for l, r, x in restrictions}

    # Initialize dp as a 2D table with n rows and h+1 columns
    dp = [[0] * (h + 1) for _ in range(n + 1)]

    # Iterate over each spot
    for i in range(1, n + 1):
        # Iterate over each possible height
        for j in range(h, -1, -1):
            # If this spot can be empty, consider that option
            if j == 0:
                dp[i][j] = dp[i-1][j]
            else:
                # Calculate the profit if we build a house at this spot
                profit = j ** 2 + dp[i-1][j]

                # Try to find the maximum height that satisfies all restrictions
                # for the current spot
                for k in range(j, -1, -1):
                    if (i, i, k) not in restriction_set:
                        # Update the dp table if a higher profit is found
                        dp[i][j] = max(dp[i][j], profit)
                        break

