def get_progress_bar_values(n, sizes):
    total_size = sum(sizes)
    progress_bar_values = []
    for i in range(1, n + 1):
        current_size = sizes[i - 1]
        for j in range(1, current_size + 1):
            progress_bar_values.append((i - 1) * current_size // total_size * 100 + j // current_size * 100)
            if (j - 1) % current_size == 0:
                progress_bar_values[-1] = (i - 1) * current_size // total_size * 100
    return progress_bar_values

def solve():
    n = int(input())
    sizes = list(map(int, input().split()))
    progress_bar_values = get_progress_bar_values(n, sizes)
    unique_values = sorted(set(progress_bar_values))
    print(' '.join(str(x) for x in unique_values))

for _ in range(1):
    solve()
