def solve():
    n = int(input())
    a = list(map(int, input().split()))

    def check(x):
        total_size = 0
        for i in range(n):
            if x == 0:
                return True
            file_size = a[i]
            target_size = (x * a[i]) // 100
            for j in range(min(file_size, target_size)):
                total_size += 1
                if (total_size * 100) // (sum(a)) == x:
                    return True
        return False

    result = [i for i in range(101) if check(i)]
    print(*result)

if __name__ == "__main__":
    tests = ['1\n6', '2\n100 500', '4\n1000 2 2 998', '6\n170 130 400 256 30 100']
    for test in tests:
