def check_string(s):
    a_count = s.count('a')
    b_count = s.count('b')
    c_count = s.count('c')

    if a_count == 0 or b_count == 0:
        return "NO"

    if c_count == a_count or c_count == b_count:
        left = 0
        right = a_count + b_count
        while left < right:
            mid = (left + right) // 2
            a_in_range = sum(1 for i in range(left, mid) if s[i] == 'a')
            b_in_range = sum(1 for i in range(left, mid) if s[i] == 'b')
            c_in_range = sum(1 for i in range(left, mid) if s[i] == 'c')

            if a_in_range == a_count and b_in_range == b_count:
                return "YES"
            elif a_in_range >= a_count or b_in_range >= b_count:
                right = mid
            else:
                left = mid + 1

        return "NO"
    else:
        return "NO"


