def solve():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))

    dp = [[0] * (n + 1) for _ in range(k + 1)]
    for i in range(1, k + 1):
        dp[i][0] = 0

    for j in range(1, n + 1):
        for i in range(1, min(j, k) + 1):
            dp[i][j] = dp[i][j - 1]
            dp[i][j] = max(dp[i][j], dp[i - 1][j - 1] + a[j - 1])

    ans = []
    j = n
    for i in range(k, 0, -1):
        if dp[i][j] == dp[i][j - 1]:
            continue
        start = j
        while j >= 0 and dp[i][j] == dp[i][j - 1]:
            j -= 1
        ans.append(j - start + 1)
        j -= 1

    print(dp[k][n])
    print(*ans)

