def longest_increasing_subsequence(a):
    n = len(a)
    max_length = 1
    current_length = 1

    for i in range(1, n):
        if a[i] > a[i - 1]:
            current_length += 1
            max_length = max(max_length, current_length)
        else:
            current_length = 1

    return max_length


def longest_subsegment_with_one_change(a):
    n = len(a)
    max_length = 1

    # Check if the subsequence is already increasing
    for i in range(n - 1):
        if a[i] >= a[i + 1]:
            max_length = max(max_length, 2)

    # Check all pairs of adjacent elements
    for i in range(n - 1):
        if a[i] < a[i + 1]:
            # If we can change one element to make the subsequence increasing
            # then we take the maximum length of the subsequence without this pair and the subsequence with this pair
            max_length = max(max_length, longest_increasing_subsequence(a[:i] + [max(a[i], a[i + 1])] + a[i + 2:]) + 1,
                             longest_increasing_subsequence(a[:i] + [min(a[i], a[i + 1])] + a[i + 2:]) + 1)

    return max_length


n = int(input())
a = list(map(int, input().split()))

print(longest_subsegment_with_one_change(a))
