def longest_increasing_subsequence(a):
    n = len(a)
    if n <= 2:
        return n

    left, right = [0] * n, [0] * n
    left[0], right[n - 1] = 1, 1

    for i in range(1, n):
        if a[i] > a[i - 1]:
            left[i] = left[i - 1] + 1
        else:
            left[i] = 1

    for i in range(n - 2, -1, -1):
        if a[i] < a[i + 1]:
            right[i] = right[i + 1] + 1
        else:
            right[i] = 1

    max_length = 0
    for i in range(n):
        if a[i] >= right[i] and a[i] + 1 >= left[i]:
            max_length = max(max_length, left[i] + right[i] - 1)
        elif a[i] + 1 >= left[i]:
            max_length = max(max_length, right[i])
        elif a[i] >= right[i]:
            max_length = max(max_length, left[i])

    return max_length


