def longest_increasing_subsequence(a):
    n = len(a)
    max_length = 1
    current_length = 1

    for i in range(1, n):
        if a[i] > a[i - 1]:
            current_length += 1
            max_length = max(max_length, current_length)
        else:
            current_length = 1

    return max_length


def can_change_to_one_element_subsequence(a):
