def longest_increasing_subsequence(a):
    n = len(a)
    if n <= 2:
        return n

    # Initialize dynamic programming arrays
    dp = [[1] * n for _ in range(2)]

    # Fill the first row of the dynamic programming table
    for i in range(1, n):
        if a[i] > a[i - 1]:
            dp[0][i] = dp[0][i - 1] + 1

    # Fill the second row of the dynamic programming table
    max_so_far = 1
    for i in range(1, n):
        for j in range(i):
            if a[i] > a[j] and (dp[0][i - 1] + 1) > dp[1][j]:
                dp[1][i] = dp[0][i - 1] + 1
                max_so_far = max(max_so_far, dp[1][i])
            elif a[i] == a[j] and (dp[0][i - 1] + 1) > dp[1][j]:
                dp[1][i] = dp[0][i - 1] + 1
                max_so_far = max(max_so_far, dp[1][i])

    return max_so_far


