import math

def get_divisors(n):
    if n == 0:
        return []
    factors = {}
    temp = n
    i = 2
    while i * i <= temp:
        while temp % i == 0:
            factors[i] = factors.get(i, 0) + 1
            temp = temp // i
        i += 1
    if temp > 1:
        factors[temp] = 1
    # Generate all divisors.
    divisors = [1]
    for p in factors:
        exponents = [p**e for e in range(factors[p] + 1)]
        current_divisors = []
        for d in divisors:
            for exp in exponents:
                current_divisors.append(d * exp)
        divisors = current_divisors
    # Remove duplicates and sort.
    divisors = list(set(divisors))
    divisors.sort()
    return divisors

a, b = map(int, input().split())
d = abs(a - b)

if d == 0:
    print(0)
else:
    divisors = get_divisors(d)
    min_lcm = float('inf')
    best_k = 0
    for g in divisors:
        x = ((a + g - 1) // g) * g
        y = x + d
        current_gcd = math.gcd(x, y)
        current_lcm = (x * y) // current_gcd
        if current_lcm < min_lcm:
            min_lcm = current_lcm
            best_k = x - a
        elif current_lcm == min_lcm:
            if (x - a) < best_k:
                best_k = x - a
    print(best_k)
