import math

def gcd(a, b):
    """Compute the greatest common divisor of a and b"""
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    """Compute the lowest common multiple of a and b"""
    return a * b // gcd(a, b)

def solve():
    """Solve the problem"""
    a, b = map(int, input().split())
    k = 0
    min_lcm = float('inf')

    while True:
        current_lcm = lcm(a + k, b + k)
        if current_lcm == min_lcm:
            break
        elif current_lcm < min_lcm:
            min_lcm = current_lcm
        k += 1

