from sys import stdin, stdout

def dfs(node, visited, graph):
    """Perform DFS from the given node."""
    stack = [(node, [node])]
    while stack:
        n, path = stack.pop()
        if not visited[n]:
            visited[n] = True
            for neighbor in graph[n]:
                if neighbor not in path:
                    stack.append((neighbor, path + [neighbor]))

def find_odd_cycle(graph):
