MOD = 998244353

def main():
    import sys
    N, K = map(int, sys.stdin.readline().split())

    if K == 0:
        print(1)
        return

    # Precompute f(m) = number of sets with max m
    f = [0] * (K + 2)
    f[0] = 1
    for m in range(1, K+1):
        f[m] = pow(2, m-1, MOD)

    # Initialize DP
    dp_prev = [0] * (K + 2)
    for m in range(K + 1):
        dp_prev[m] = f[m]

    for i in range(2, N + 1):
        dp_current = [0] * (K + 2)
        pre_sum = [0] * (K + 2)
        for m in range(K + 1):
            if m == 0:
                pre_sum[m] = dp_prev[m]
            else:
                pre_sum[m] = (pre_sum[m - 1] + dp_prev[m]) % MOD
            if m > 0:
                sum_before = pre_sum[m - 1]
                dp_current[m] = (sum_before * f[m]) % MOD
        dp_prev = dp_current

    total = sum(dp_prev) % MOD
    print(total)

if __name__ == "__main__":
    main()
