from sys import stdin, stdout

def dfs(u, parent, graph, edges):
    global visited
    global direction
    visited[u] = True
    for v in range(len(graph[u])):
        if graph[u][v] > 0:
            if not visited[v]:
                dfs(v, u, graph, edges)
            elif v != parent:
                if graph[u][v] < graph[v][u]:
                    direction[edges[(u-1)*n + v]] = 1
                else:
                    direction[edges[(u-1)*n + v]] = 0

def solve():
    global n, m, graph, edges, direction, visited
    n, m = map(int, stdin.readline().split())
    graph = [[0]*n for _ in range(n)]
    edges = []
    for i in range(m):
        a, b, c = map(int, stdin.readline().split())
        graph[a-1][b-1] = c
        graph[b-1][a-1] = c
        edges.append(i)
    direction = [0]*m
    visited = [False]*n
    dfs(0, -1, graph, edges)
    for i in range(m):
        stdout.write(str(direction[i]) + "\n")

solve()
