from collections import deque

def solve():
    n, m = map(int, input().split())
    adj = [[] for _ in range(n)]
    flow = [[0, 0] for _ in range(m)]

    for i in range(m):
        u, v, c = map(int, input().split())
        u -= 1
        v -= 1
        adj[u].append((v, i, c))
        adj[v].append((u, i, 0))

    parent = [-1] * n
    vis = [False] * n

    def bfs(s, t, par):
        queue = deque()
        queue.append(s)
        vis[s] = True
        while queue:
            u = queue.popleft()
            for v, i, c in adj[u]:
                if not vis[v] and c > 0:
                    vis[v] = True
                    parent[v] = u
                    queue.append(v)
        return parent[t] != -1

    def dfs(u, t, mf):
        if u == t:
            return mf
        for v, i, c in adj[u]:
            if vis[v] and c > 0:
                res = dfs(v, t, min(mf, c))
                if res > 0:
                    flow[i][0] = res
                    flow[i][1] = res
                    return res
        return 0

    s = 0
    t = n - 1
    total_flow = 0
    while bfs(s, t, parent):
        while True:
            mf = dfs(s, t, float('inf'))
            if mf == 0:
                break
            total_flow += mf

    for i in range(m):
        u, v, c = flow[i]
        print(0 if u == v else 1)

solve()
