from sys import stdin, stdout

def dfs(u, parent):
    global time
    visited[u] = True
    for v, c in adj_list[u]:
        if not visited[v]:
            time += 1
            par[v] = (u, c)
            dfs(v, u)
        elif v != parent:
            if flow[u][v] + c != 0:
                edge_direction[v][u] = 1
                edge_direction[u][v] = 0

n, m = map(int, stdin.readline().split())
adj_list = [[] for _ in range(n+1)]
edge_direction = [[0 for _ in range(n+1)] for _ in range(n+1)]
flow = [[0 for _ in range(n+1)] for _ in range(n+1)]
par = [[None, None] for _ in range(n+1)]
visited = [False for _ in range(n+1)]
time = 0

for i in range(m):
    a, b, c = map(int, stdin.readline().split())
    adj_list[a].append((b, c))
    adj_list[b].append((a, c))
    flow[a][b] = c
    flow[b][a] = 0

dfs(1, -1)

for i in range(2, n):
    if par[i][0] is not None:
        flow[par[i][0][0]][i] -= par[i][1]
        flow[i][par[i][0][0]] += par[i][1]

for i in range(1, n+1):
    for j in range(1, n+1):
        if i != j and flow[i][j] != 0:
            print("Error: Flow is not conserved at vertex " + str(i) + " with edge to " + str(j))

for i in range(m):
    a, b, c = map(int, stdin.readline().split())
    stdout.write(str(edge_direction[a][b]) + '\n')
