import sys

def solve():
    n, m = map(int, input().split())
    items = []
    for _ in range(n):
        w, c = map(int, input().split())
        items.append((w, c))

    # Sort items by weight-to-cost ratio in descending order
    items.sort(key=lambda x: (x[0], -x[1]))

    total_cost = 0
    current_weight = 0
    for weight, cost in items:
        if current_weight + weight <= m:
            total_cost += cost
            current_weight += weight
        else:
            remaining_weight = m - current_weight
            total_cost += cost * (remaining_weight // weight) + max(cost, cost - (cost * (remaining_weight % weight)) // weight)
            break

    print(total_cost)

if __name__ == "__main__":
    multi_test = False
    if multi_test:
        t = int(input())
        for _ in range(t):
            solve()
    else:
        solve()
