def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))

    current_house = 1
    total_time = 0

    for task_house in a:
        if task_house >= current_house:
            time_to_move = task_house - current_house
        else:
            time_to_move = (n - current_house) + task_house

        total_time += time_to_move
        current_house = task_house

    print(total_time)

if __name__ == '__main__':
    solve()
