import sys
import ast
from itertools import permutations

note_to_num = {
    'C': 0, 'C#': 1, 'D': 2, 'D#': 3, 'E': 4,
    'F': 5, 'F#': 6, 'G': 7, 'G#': 8, 'A': 9,
    'B': 10, 'H': 11
}

def classify(triad):
    nums = []
    for n in triad.split():
        if n not in note_to_num:
            return "strange"
        nums.append(note_to_num[n])
    for a, b, c in permutations(nums, 3):
        d1 = (b - a) % 12
        d2 = (c - b) % 12
        if d1 == 4 and d2 == 3:
            return "major"
        if d1 == 3 and d2 == 4:
            return "minor"
    return "strange"

data = sys.stdin.read().strip()
if not data:
    sys.exit(0)

results = []
data_stripped = data.strip()
# If input is a Python-style list like ['C E G', 'C# B F']
if data_stripped.startswith('['):
    try:
        parsed = ast.literal_eval(data_stripped)
        if isinstance(parsed, list):
            for item in parsed:
                results.append(classify(item))
        else:
            # fallback to treating whole as one line
            results.append(classify(data_stripped))
    except Exception:
        # fallback
        for line in data.splitlines():
            line = line.strip()
            if line:
                results.append(classify(line))
else:
    lines = [line.strip() for line in data.splitlines() if line.strip()]
    if len(lines) == 1:
        # single test: could be three notes or multiple cases separated by commas
        line = lines[0]
        # if comma separated list of quoted strings
        if line.count("'") >= 2 or line.count('"') >= 2:
            try:
                parsed = ast.literal_eval(line)
                if isinstance(parsed, list):
                    for item in parsed:
                        results.append(classify(item))
                else:
                    results.append(classify(line))
            except Exception:
                # normal single triad
                results.append(classify(line))
        else:
            results.append(classify(line))
    else:
        for line in lines:
            results.append(classify(line))

# Print results: one per line
print("\n".join(results))

