    if t2:
        clean_tokens.append(t2)

# collect valid note tokens into groups of 3
valid = set(notes_order)
groups = []
cur = []
for t in clean_tokens:
    if t in valid:
        cur.append(t)
        if len(cur) == 3:
            groups.append(list(cur))
            cur = []
# if nothing found but maybe the first non-empty line contains tokens separated by spaces including invalid punctuation,
# try fallback: take first non-empty line and split by spaces, cleaning each token
