    for p in permutations([a,b,c]):
        i0 = idx[p[0]]
        i1 = idx[p[1]]
        i2 = idx[p[2]]
        d1 = (i1 - i0) % 12
        d2 = (i2 - i1) % 12
        if d1 == 4 and d2 == 3:
            return "major"
        if d1 == 3 and d2 == 4:
            return "minor"
    return "strange"

results = [classify(g) for g in groups]

