import sys

data = sys.stdin.read().splitlines()
if not data:
    sys.exit(0)
n = int(data[0].strip())
arr = [line.strip() for line in data[1:1 + 2 * n - 2]]

longs = [s for s in arr if len(s) == n - 1]
a, b = longs[0], longs[1]
candidates = [a + b[-1], b + a[-1]]

def try_candidate(s):
    needP = {}
    needS = {}
    for l in range(1, n):
        p = s[:l]
        q = s[n - l:]
        needP[p] = needP.get(p, 0) + 1
        needS[q] = needS.get(q, 0) + 1
    res = []
    for t in arr:
        if needP.get(t, 0) > 0:
            res.append('P')
            needP[t] -= 1
        elif needS.get(t, 0) > 0:
            res.append('S')
            needS[t] -= 1
        else:
            return None
    return ''.join(res)

for cand in candidates:
    ans = try_candidate(cand)
    if ans is not None:
        print(ans)
        break

