import sys

data = sys.stdin.read().strip().splitlines()
if not data:
    sys.exit(0)
n = int(data[0].strip())
m = 2 * n - 2
arr = [line.strip() for line in data[1:1 + m]]

by_len = {}
for i, s in enumerate(arr):
    l = len(s)
    by_len.setdefault(l, []).append((s, i))

# two candidates from the two strings of length n-1
big = by_len[n - 1]
sA = big[0][0]
sB = big[1][0]
candidates = [sA + sB[-1], sB + sA[-1]]

def try_candidate(candidate):
    res = [''] * m
    for k in range(1, n):
        p = candidate[:k]
        q = candidate[n - k:]
        lst = by_len[k]
        a, ai = lst[0]
        b, bi = lst[1]
        aP = (a == p); aS = (a == q)
        bP = (b == p); bS = (b == q)
        if not ((aP or bP) and (aS or bS)):
            return None
        if aP and not aS:
            res[ai] = 'P'; res[bi] = 'S'
        elif bP and not bS:
            res[bi] = 'P'; res[ai] = 'S'
        elif aS and not aP:
            res[ai] = 'S'; res[bi] = 'P'
        elif bS and not bP:
            res[bi] = 'S'; res[ai] = 'P'
        else:
            # both match both (or both match p==q), choose arbitrarily
            res[ai] = 'P'; res[bi] = 'S'
    return ''.join(res)

for cand in candidates:
    ans = try_candidate(cand)
    if ans is not None:
        print(ans)
        break

