if not data:
    sys.exit(0)

out_lines = []
for i in range(0, len(data), 3):
    w, h, k = data[i], data[i+1], data[i+2]
    total = 0
    for j in range(k):
        wi = w - 4*j
        hi = h - 4*j
        if wi <= 0 or hi <= 0:
            break
        if wi == 1 or hi == 1:
            total += wi * hi
        else:
            total += 2 * (wi + hi) - 4
    out_lines.append(str(total))

sys.stdout.write("\n".join(out_lines))

