    total = 0
    for i in range(k):
        wi = w - 4 * i
        hi = h - 4 * i
        if wi <= 0 or hi <= 0:
            break
        if wi == 1 or hi == 1:
            total += wi * hi
        else:
            total += 2 * (wi + hi) - 4
    outs.append(str(total))

