import sys, re

data = sys.stdin.read()
tiles = re.findall(r'[1-9][mps]', data)
if len(tiles) < 3:
    # fallback: split whitespace and take first three tokens
    parts = data.split()
    tiles = [p for p in parts if len(p) >= 2][:3]
tiles = tiles[:3]

def num(t): return int(t[0])
def suit(t): return t[1]

# check 0 (already a mentsu)
# koutsu
if tiles[0] == tiles[1] == tiles[2]:
    print(0)
    sys.exit(0)
# shuntsu
if suit(tiles[0]) == suit(tiles[1]) == suit(tiles[2]):
    nums = sorted([num(t) for t in tiles])
    if nums[0]+1 == nums[1] and nums[1]+1 == nums[2]:
        print(0)
        sys.exit(0)

# check 1 (can complete with one draw)
ans = 2
for i in range(3):
    for j in range(i+1, 3):
        a, b = tiles[i], tiles[j]
        if a == b:
            ans = min(ans, 1)
        elif suit(a) == suit(b):
            if abs(num(a) - num(b)) <= 2:
                ans = min(ans, 1)

print(ans)

