for hand in cases:
    # parse tiles
    cnt = {}
    arr = []
    for t in hand:
        t = t.strip()
        if len(t) < 2:
            continue
        num = int(t[0])
        suit = t[1]
        arr.append((num, suit))
        cnt[(num, suit)] = cnt.get((num, suit), 0) + 1

