                    found = True
    if found:
        out.append("0")
        continue

    # check if one tile needed
    need1 = False
    # if any pair identical -> need 1 to make triplet
    for k in cnt:
        if cnt[k] == 2:
            need1 = True
            break
    # check sequences needing one tile
    for (num, suit) in arr:
        for d in [0, -1, -2]:
            a = num + d
            b = a + 1
            c = a + 2
            if 1 <= a <= 9 and 1 <= b <= 9 and 1 <= c <= 9:
                have = 0
                if cnt.get((a, suit),0) >=1: have += 1
                if cnt.get((b, suit),0) >=1: have += 1
                if cnt.get((c, suit),0) >=1: have += 1
                if have == 2:
                    need1 = True
    if need1:
        out.append("1")
    else:
        out.append("2")

sys.stdout.write("\n".join(out))

