#!/usr/bin/env python3
import sys

MOD = 1000000007

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
n = int(data[0]); m = int(data[1]); g = int(data[2])

L = n + m

def precompute_fact(N):
    fact = [1] * (N+1)
    invfact = [1] * (N+1)
    for i in range(1, N+1):
        fact[i] = fact[i-1] * i % MOD
    invfact[N] = pow(fact[N], MOD-2, MOD)
    for i in range(N, 0, -1):
        invfact[i-1] = invfact[i] * i % MOD
    return fact, invfact

def comb(nc, k, fact, invfact):
    if k < 0 or k > nc or nc < 0:
        return 0
    return fact[nc] * invfact[k] % MOD * invfact[nc-k] % MOD

if m == 0:
    final_only = 1 if (n % 2 == 0) else 0
    print(1 if final_only == g else 0)
    sys.exit(0)

Nmax = n + m
fact, invfact = precompute_fact(Nmax)

ans = 0
U = min(n, L-2)
if U >= 0:
    for k in range(0, U+1):
        if (k & 1) == g:
            ans = (ans + comb(n+m-1-k, m-1, fact, invfact)) % MOD

if m == 1:
    final_last = 1 if (n % 2 == 0) else 0
    if final_last == g:
        ans = (ans + 1) % MOD

print(ans % MOD)

