data = sys.stdin.read().splitlines()
if not data:
    sys.exit(0)
try:
    n = int(data[0].strip())
except:
    sys.exit(0)
grid = []
for i in range(1, 1 + n):
    if i < len(data):
        line = data[i].strip()
    else:
        line = ""
    grid.append(line)

if any(len(row) != n for row in grid):
    print("NO")
    sys.exit(0)

diag_char = grid[0][0]
other_char = None
ok = True
for i in range(n):
    for j in range(n):
        if j == i or j == n - 1 - i:
            if grid[i][j] != diag_char:
                ok = False
                break
        else:
            if other_char is None:
                other_char = grid[i][j]
            if grid[i][j] != other_char:
                ok = False
                break
    if not ok:
        break

if other_char is None or other_char == diag_char:
    ok = False

print("YES" if ok else "NO")

