import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)

n = int(data[0])
lines = data[1:1+n]

diag_char = lines[0][0]
other_char = None
ok = True

for i in range(n):
    row = lines[i]
    if len(row) != n:
        ok = False
        break
    for j in range(n):
        if j == i or j == n - 1 - i:
            if row[j] != diag_char:
                ok = False
                break
        else:
            if other_char is None:
                other_char = row[j]
            if row[j] != other_char:
                ok = False
                break
    if not ok:
        break

if other_char == diag_char:
    ok = False

print("YES" if ok else "NO")

