import sys

tokens = sys.stdin.read().split()
if not tokens:
    sys.exit(0)
n = int(tokens[0])
grid = tokens[1:1+n]

diag = grid[0][0]
other = grid[0][1] if n > 1 else None

if diag == other:
    print("NO")
    sys.exit(0)

ok = True
for i in range(n):
    row = grid[i]
    for j, ch in enumerate(row):
        if j == i or j == n - 1 - i:
            if ch != diag:
                ok = False
                break
        else:
            if ch != other:
                ok = False
                break
    if not ok:
        break

print("YES" if ok else "NO")

