import sys

data = [line.rstrip("\n") for line in sys.stdin.readlines()]
p = 0
out_lines = []
while p < len(data):
    # skip empty lines
    if data[p].strip() == "":
        p += 1
        continue
    try:
        n = int(data[p].strip())
    except:
        break
    p += 1
    if p + n - 1 >= len(data):
        break
    grid = []
    for i in range(n):
        line = data[p + i].rstrip()
        # If line is shorter/longer, take exactly n chars or pad/truncate
        if len(line) < n:
            line = line.ljust(n)
        else:
            line = line[:n]
        grid.append(line)
    p += n

    diag_char = grid[0][0]
    other_char = grid[0][1]
    ok = True
    if diag_char == other_char:
        ok = False
    else:
        for i in range(n):
            for j in range(n):
                if j == i or j == n - 1 - i:
                    if grid[i][j] != diag_char:
                        ok = False
                        break
                else:
                    if grid[i][j] != other_char:
                        ok = False
                        break
            if not ok:
                break
    out_lines.append("YES" if ok else "NO")

if out_lines:
    sys.stdout.write("\n".join(out_lines))
else:
    # no valid cases found; do nothing
    pass

