import sys

MOD = 998244353

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    n = int(data[0])
    if n == 1 or n == 2:
        fib = 1
    else:
        a, b = 1, 1
        for _ in range(3, n + 1):
            a, b = b, (a + b) % MOD
        fib = b
    pow2 = pow(2, n, MOD)
    inv_pow2 = pow(pow2, MOD - 2, MOD)
    ans = fib * inv_pow2 % MOD
    print(ans)

if __name__ == "__main__":
    main()
