import sys

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
n, l = data[0], data[1]
a = sorted(data[2:2+n])
max_gap = 0.0
if n > 1:
    for i in range(n-1):
        gap = (a[i+1] - a[i]) / 2.0
        if gap > max_gap:
            max_gap = gap
if a:
    if a[0] > max_gap:
        max_gap = float(a[0])
    if l - a[-1] > max_gap:
        max_gap = float(l - a[-1])
print("{:.10f}".format(max_gap))

