import sys
import re

txt = sys.stdin.read()
tokens = re.findall(r"[abc]+", txt)
if tokens:
    strings = tokens
else:
    parts = txt.strip().split()
    if not parts:
        sys.exit()
    try:
        t = int(parts[0])
        strings = parts[1:1+t]
    except:
        strings = parts

out_lines = []
for s in strings:
    n = len(s)
    i = 0
    while i < n and s[i] == 'a':
        i += 1
    cnt_a = i
    j = i
    while j < n and s[j] == 'b':
        j += 1
    cnt_b = j - i
    k = j
    while k < n and s[k] == 'c':
        k += 1
    cnt_c = k - j
    if k != n or cnt_a < 1 or cnt_b < 1 or cnt_c < 1:
        out_lines.append("NO")
    else:
        out_lines.append("YES" if (cnt_c == cnt_a or cnt_c == cnt_b) else "NO")

sys.stdout.write("\n".join(out_lines))

